commits.content:
.bonus {
  padding-top: 66px;
  .container {
    grid-template-rows:83px 318px;
  }
  &__img {
    grid-column: 1/7;
  }
  &__title {
    grid-column: 6/12;
    margin-top: 46px;
    margin-left: 100px;
    font-size: 24px;
    line-height: 36px;
    letter-spacing: 0.05em;
    font-weight: 400;
    text-transform: uppercase;
    color: #3C3B3B;
  }
  &__list {
    grid-column: 7/12;
    grid-row: 2;
    margin-top: 7px;
  }
  &__item {
    position: relative;
    margin-top: 40px;
    font-style: normal;
    font-weight: 600;
    font-size: 20px;
    line-height: 30px;
    color: #1B567C;
    &:before {
      content: url("../../img/advantages/check_mark.svg");
      position: absolute;
      left: -31px;
      display: flex;
      justify-content: center;
      align-items: center;
      width: 32px;
      height: 32px;
      border-radius: 50%;
      background:  linear-gradient(90deg, #7CB4D8 0%, rgba(191, 230, 255, 0) 100%);
    }
  }
}
