.cms {
  margin-top: 89px;
  padding-top: 48px;
  padding-bottom: 86px;
  .container {
    grid-template-rows: 72px 117px 122px 115px 115px;
  }
  &__title {
    width: 1180px;
    letter-spacing: 0.04em;
    margin-top: 9px;
    grid-row: 1;
    font-weight: 400;
    font-size: 48px;
    line-height: 72px;
    text-align: center;
  }
  &__descr {
    grid-column: 1/8;
    grid-row: 2;
    margin-top: 57px;
    font-weight: 400;
    font-size: 20px;
    line-height: 30px;
    color: #3C3B3B;
  }
  .row {
    display: flex;
    justify-content: space-between;
    &_first {
      margin-top: 73px;
      grid-row: 3;
      grid-column: 1/12;
    }
    &_second {
      grid-row: 4;
      grid-column: 1/12;
      align-self: end;
    }
    &_third {
      grid-row: 5;
      grid-column: 1/12;
      align-self: center;
    }
  }
}
