commits.content:
.domains {
  margin-top: 94px;
  padding-top: 52px;
  .container {
    grid-template-rows: 124px 90px 90px 90px 90px 90px;
    row-gap: 21px;
  }
  &__title {
    grid-row: 1;
    width: 1180px;
    text-align: center;
    font-size: 48px;
    line-height: 72px;
    font-weight: 400;
    letter-spacing: 0.04em;
    color: #3C3B3B;
  }
  &__descr {
    grid-column: 1/6;
    grid-row: 3/5;
  }
  &__btn {
    grid-column: 1;
    grid-row: 6;
  }
  .ru {
    grid-column: 7/9;
    grid-row: 2;
  }
  .su {
    grid-column: 7/9;
    grid-row: 3;
  }
  .net {
    grid-column: 7/9;
    grid-row: 4;
  }
  .biz {
    grid-column: 7/9;
    grid-row: 5;
  }
  .moscow {
    grid-column: 7/9;
    grid-row: 6;
  }
  .rf {
    grid-column: 10/12;
    grid-row: 2;
  }
  .com {
    grid-column: 10/12;
    grid-row: 3;
  }
  .org {
    grid-column: 10/12;
    grid-row: 4;
  }
  .info {
    grid-column: 10/12;
    grid-row: 5;
  }
  .msc {
    grid-column: 10/12;
    grid-row: 6;
  }
  &__item {
    position: relative;
    width: 280px;
    height: 90px;
    background-color: $color-white;
    box-shadow: 0px 4px 4px rgba(183, 183, 183, 0.43);
    border-radius: 5px;
    &-icon {
      position: absolute;
      left: 20px;
      top: 50%;
      transform: translateY(-50%);
      width: 55px;
      height: 55px;
      border-radius:50%;
      background: linear-gradient(90deg, #7CB4D8 0%, rgba(191, 230, 255, 0) 100%);
    }
    &-title {
      position:absolute;
      left: 39px;
      top: 50%;
      transform: translateY(-50%);
      font-size: 24px;
      line-height: 36px;
      letter-spacing: 0.05em;
      font-weight: 600;
      text-transform: uppercase;
      color: #1B567C;
      z-index: 2;
    }
    &-price {
      margin-top: 18px;
      margin-right: 19px;
      font-size: 24px;
      font-weight: 600;
      text-align: end;
      letter-spacing: 0.05em;
      text-transform: uppercase;
      color: #3C3B3B;
    }
    &-text {
      margin-right: 13px;
      margin-bottom: 16px;
      font-size: 20px;
      line-height: 30px;
      font-weight: 400;
      color: #3C3B3B;
      text-transform: none;
      letter-spacing: normal;
    }

  }
}
